foreignObject.div:
.progressbar__svg-circle_html {
  stroke: rgb(216, 73, 36);
}

.progressbar__svg-circle_css {
  stroke: rgb(0, 106, 177);
}

.progressbar__svg-circle_scss {
  stroke: hsl(300, 100%, 60%);
}


.prog_circle_text_color_html {
  color: rgb(216, 73, 36);
}

.prog_circle_text_color_css {
  color: rgb(0, 106, 177);
}

.prog_circle_text_color_scss {
  color: hsl(300, 100%, 60%);
}

.shadow-html {
  filter: drop-shadow(0 0 5px rgb(241, 115, 84));
}
.shadow-css {
  filter: drop-shadow(0 0 5px rgb(0, 106, 177));
}
.shadow-scss {
  filter: drop-shadow(0 0 5px hsl(300, 100%, 60%));
}

.circle-html {
  animation: anim_circle-html 4s ease-in-out forwards;
}

.circle-css {
  animation: anim_circle-css 4s ease-in-out forwards;
}
.circle-scss {
  animation: anim_circle-scss 4s ease-in-out forwards;
}

@keyframes anim_circle-html {
    to {
      stroke-dashoffset: 50.2;
    }
  }

  @keyframes anim_circle-css {
    to {
      stroke-dashoffset: 75.3;
    }
  }
  @keyframes anim_circle-scss {
    to {
      stroke-dashoffset: 200.8;
    }
  }









